mapper.select:
SELECT * FROM (( SELECT DISTINCT ON(a.audit_id) a.barcode AS s_barcode, COALESCE(i.barcode, i.alt_barcode) AS i_barcode, c.container_id, c.path_cache AS path FROM audit AS a LEFT OUTER JOIN inventory AS i ON ( a.barcode = i.barcode OR a.barcode = i.alt_barcode OR ('GMC-' || a.barcode) = i.barcode ) LEFT OUTER JOIN container AS c ON c.container_id = i.container_id WHERE a.audit_group_id = #{audit_group_id} ORDER BY a.audit_id
, i.container_id = #{container_id} DESC
) UNION ( SELECT DISTINCT ON(i.barcode, i.alt_barcode) a.barcode AS s_barcode, COALESCE(i.barcode, i.alt_barcode) AS i_barcode, c.container_id, c.path_cache AS path FROM inventory AS i LEFT OUTER JOIN audit AS a ON ( ( a.barcode = i.barcode OR a.barcode = i.alt_barcode OR ('GMC-' || a.barcode) = i.barcode ) AND a.audit_group_id = #{audit_group_id} ) LEFT OUTER JOIN container AS c ON c.container_id = i.container_id
WHERE i.container_id = #{container_id}
)) AS q ORDER BY s_barcode, i_barcode
