mapper.select:
SELECT bh.borehole_id, bh.prospect_id, bh.name AS borehole_name, bh.alt_names AS alt_borehole_names, bh.is_onshore, bh.completion_date,
bh.measured_depth, mdu.unit_id AS mdu_unit_id, mdu.abbr AS mdu_unit_abbr, mdu.name AS mdu_unit_name,
bh.elevation, evu.unit_id AS evu_unit_id, evu.abbr AS evu_unit_abbr, evu.name AS evu_unit_name FROM borehole AS bh LEFT OUTER JOIN unit AS mdu ON mdu.unit_id = measured_depth_unit_id LEFT OUTER JOIN unit AS evu ON evu.unit_id = elevation_unit_id WHERE bh.borehole_id = #{borehole_id}
