mapper.insert:
INSERT INTO container (
	barcode, alt_barcode, name, remark
) VALUES (
	#{barcode}, #{alt_barcode}, #{name}, #{remark}
)

mapper.select:
SELECT container_id
FROM container
WHERE barcode = #{barcode}
	OR barcode = ('GMC-' || #{barcode})
	OR alt_barcode = #{barcode}

mapper.update:
UPDATE container SET parent_container_id = #{container_id}
WHERE barcode = #{barcode}
	OR barcode = ('GMC-' || #{barcode})
	OR alt_barcode = #{barcode}

mapper.select:
WITH RECURSIVE r AS ( SELECT container_id, path_cache FROM container WHERE barcode = #{barcode} OR alt_barcode = #{barcode} OR barcode = 'GMC-' || #{barcode}
UNION ALL
SELECT co.container_id, co.path_cache FROM r JOIN container AS co ON r.container_id = co.parent_container_id ) SELECT r.container_id, r.path_cache AS name, COUNT(DISTINCT COALESCE(iv.barcode, iv.alt_barcode, '*')) AS total FROM r JOIN inventory AS iv ON iv.container_id = r.container_id GROUP BY r.container_id, r.path_cache ORDER BY r.path_cache
