mapper.insert:
INSERT INTO container (
	container_type_id, barcode, alt_barcode, name, remark
) VALUES (
	#{type.id}, #{barcode}, #{alt_barcode}, #{name}, #{remark}
)

mapper.select:
SELECT container_id
FROM container
WHERE barcode = #{barcode}
	OR barcode = ('GMC-' || #{barcode})
	OR alt_barcode = #{barcode}

mapper.update:
UPDATE container SET parent_container_id = #{container_id}
WHERE barcode = #{barcode}
	OR barcode = ('GMC-' || #{barcode})
	OR alt_barcode = #{barcode}

mapper.select:
SELECT container_type_id, name
FROM container_type
WHERE container_type_id = #{container_type_id}

( SELECT c.container_id, c.path_cache, true AS include, COUNT(DISTINCT COALESCE(iv.barcode, iv.alt_barcode)) AS total FROM container AS c LEFT OUTER JOIN inventory AS iv ON iv.container_id = c.container_id WHERE (iv.active OR iv.active IS NULL) AND c.container_id IN ( WITH RECURSIVE r AS ( SELECT container_id FROM container WHERE COALESCE(barcode, alt_barcode) = #{barcode}
UNION ALL
SELECT co.container_id FROM r JOIN container AS co ON r.container_id = co.parent_container_id ) SELECT container_id FROM r ) GROUP BY c.container_id, c.path_cache ORDER BY c.path_cache ) UNION ( SELECT c.container_id, c.path_cache, false AS include, COUNT(DISTINCT COALESCE(iv.barcode, iv.alt_barcode)) AS total FROM inventory AS iv JOIN container AS c ON c.container_id = iv.container_id WHERE iv.active AND COALESCE(iv.barcode, iv.alt_barcode) = #{barcode} GROUP BY c.container_id, c.path_cache ORDER BY c.path_cache )

SELECT co.name AS collection,
	COUNT(DISTINCT COALESCE(iv.barcode, iv.alt_barcode)) AS total
FROM inventory AS iv
JOIN collection AS co
	ON co.collection_id = iv.collection_id
WHERE iv.active AND (
	COALESCE(iv.barcode, iv.alt_barcode) = #{barcode}

	OR iv.container_id IN ${i}

)
GROUP BY co.name

SELECT we.name AS well, COUNT(DISTINCT COALESCE(iv.barcode, iv.alt_barcode)) AS total FROM container AS co JOIN inventory AS iv ON iv.container_id = co.container_id JOIN inventory_well AS ivw ON ivw.inventory_id = iv.inventory_id JOIN well AS we ON we.well_id = ivw.well_id WHERE iv.active AND ( COALESCE(iv.barcode, iv.alt_barcode) = #{barcode}
OR iv.container_id IN ${i}
) GROUP BY we.name

SELECT ( COALESCE(ps.name, '') || CASE WHEN ps.name IS NULL THEN '' ELSE ' - ' END || bh.name ) AS borehole, COUNT(DISTINCT COALESCE(iv.barcode, iv.alt_barcode)) AS total FROM container AS co JOIN inventory AS iv ON iv.container_id = co.container_id JOIN inventory_borehole AS ivb ON ivb.inventory_id = iv.inventory_id JOIN borehole AS bh ON bh.borehole_id = ivb.borehole_id LEFT OUTER JOIN prospect AS ps ON ps.prospect_id = bh.prospect_id WHERE iv.active AND ( COALESCE(iv.barcode, iv.alt_barcode) = #{barcode}
OR iv.container_id IN ${i}
) GROUP BY ps.name, bh.name ORDER BY ps.name, bh.name

SELECT STRING_AGG(k.keyword::text, ', ')
FROM (
	SELECT DISTINCT UNNEST(i.keywords) AS keyword
	FROM inventory AS i
	WHERE i.active AND (
		COALESCE(i.barcode, i.alt_barcode) = #{barcode}

		OR i.container_id IN ${i}

	)
	ORDER BY keyword
) AS k

SELECT sl.name AS shotline, COUNT(DISTINCT COALESCE(iv.barcode, iv.alt_barcode)) AS total FROM container AS co JOIN inventory AS iv ON iv.container_id = co.container_id JOIN inventory_shotpoint AS ivs ON ivs.inventory_id = iv.inventory_id JOIN shotpoint AS sp ON sp.shotpoint_id = ivs.shotpoint_id JOIN shotline AS sl ON sl.shotline_id = sp.shotline_id WHERE iv.active AND ( COALESCE(iv.barcode, iv.alt_barcode) = #{barcode}
OR iv.container_id IN ${i}
) GROUP BY co.container_id, sl.shotline_id, sl.name
