mapper.select:
SELECT iv.inventory_id, iv.sample_number, iv.barcode, iv.alt_barcode, iv.box_number, iv.set_number, iv.core_number, iv.state_number, iv.description, iv.remark, iv.interval_top, iv.interval_bottom, iv.keywords::text[], iv.radiation_msvh, iv.received_date, iv.entered_date, iv.modified_date, iv.modified_user, iun.unit_id AS interval_unit_id, iun.name AS interval_unit_name, iun.abbr AS interval_unit_abbr, cl.collection_id, cl.name AS collection_name, cl.description AS collection_description, co.path_cache AS container_path FROM inventory AS iv LEFT OUTER JOIN collection AS cl ON cl.collection_id = iv.collection_id LEFT OUTER JOIN unit AS iun ON iun.unit_id = iv.interval_unit_id LEFT OUTER JOIN container AS co ON co.container_id = iv.container_id WHERE iv.active AND (iv.barcode = #{barcode} OR iv.barcode = ('GMC-' || #{barcode}) OR iv.alt_barcode = #{barcode} OR iv.container_id IN ( WITH RECURSIVE r AS ( SELECT container_id FROM container WHERE barcode = #{barcode}
UNION ALL
SELECT co.container_id FROM r JOIN container AS co ON r.container_id = co.parent_container_id ) SELECT container_id FROM r )) LIMIT 100

mapper.update:
UPDATE inventory SET container_id = #{container_id}
WHERE barcode = #{barcode}
	OR barcode = ('GMC-' || #{barcode})
	OR alt_barcode = #{barcode}

UPDATE inventory AS i
	SET container_id = #{dest}
FROM container AS c
WHERE c.container_id = i.container_id
	AND (
		c.barcode = #{src}
		OR c.barcode = ('GMC-' || #{src})
		OR c.alt_barcode = #{src}
	)

mapper.insert:
INSERT INTO inventory (
	barcode, remark, container_id

	,keywords

) VALUES (
	#{barcode}, #{remark}, #{container.id}

	,#{i}

)
