mapper.select:
SELECT iv.inventory_id, iv.sample_number, iv.barcode, iv.alt_barcode, iv.box_number, iv.set_number, iv.core_number, iv.state_number, iv.description, iv.remark, iv.interval_top, iv.interval_bottom, iun.unit_id AS interval_unit_id, iun.name AS interval_unit_name, iun.abbr AS interval_unit_abbr, cl.collection_id, cl.name AS collection_name, cl.description AS collection_description, co.path_cache AS container_path FROM inventory AS iv LEFT OUTER JOIN collection AS cl ON cl.collection_id = iv.collection_id LEFT OUTER JOIN unit AS iun ON iun.unit_id = iv.interval_unit_id LEFT OUTER JOIN container AS co ON co.container_id = iv.container_id WHERE iv.barcode = #{barcode} OR iv.barcode = ('GMC-' || #{barcode}) OR iv.alt_barcode = #{barcode} OR iv.container_id IN ( WITH RECURSIVE r AS ( SELECT container_id FROM container WHERE barcode = #{barcode}
UNION ALL
SELECT co.container_id FROM r JOIN container AS co ON r.container_id = co.parent_container_id ) SELECT container_id FROM r ) LIMIT 100

SELECT DISTINCT inventory_id FROM inventory_geog
WHERE ST_Intersects(
	geog, ST_Transform(ST_GeomFromText(#{wkt}, 3857), 4326)
) LIMIT ${limit}

mapper.update:
UPDATE inventory SET container_id = #{container_id}
WHERE barcode = #{barcode}
	OR barcode = ('GMC-' || #{barcode})
	OR alt_barcode = #{barcode}
